-- Recalculate data_completeness_pct with ingredients_raw now populated
-- ═══════════════════════════════════════════════════════════════════════════
UPDATE scores sc SET
  data_completeness_pct = round(100.0 * (
    (CASE WHEN nf.calories        IS NOT NULL AND nf.calories::text        NOT IN ('N/A','') THEN 1 ELSE 0 END) * 10 +
    (CASE WHEN nf.total_fat_g     IS NOT NULL AND nf.total_fat_g::text     NOT IN ('N/A','') THEN 1 ELSE 0 END) * 10 +
    (CASE WHEN nf.saturated_fat_g IS NOT NULL AND nf.saturated_fat_g::text NOT IN ('N/A','') THEN 1 ELSE 0 END) * 15 +
    (CASE WHEN nf.carbs_g         IS NOT NULL AND nf.carbs_g::text         NOT IN ('N/A','') THEN 1 ELSE 0 END) *  5 +
    (CASE WHEN nf.sugars_g        IS NOT NULL AND nf.sugars_g::text        NOT IN ('N/A','') THEN 1 ELSE 0 END) * 15 +
    (CASE WHEN nf.protein_g       IS NOT NULL AND nf.protein_g::text       NOT IN ('N/A','') THEN 1 ELSE 0 END) *  5 +
    (CASE WHEN nf.salt_g          IS NOT NULL AND nf.salt_g::text          NOT IN ('N/A','') THEN 1 ELSE 0 END) * 15 +
    (CASE WHEN nf.trans_fat_g     IS NOT NULL AND nf.trans_fat_g::text     NOT IN ('N/A','') THEN 1 ELSE 0 END) * 10 +
    (CASE WHEN nf.fibre_g         IS NOT NULL AND nf.fibre_g::text         NOT IN ('N/A','') THEN 1 ELSE 0 END) *  5 +
    (CASE WHEN i.additives_count  IS NOT NULL AND i.additives_count::text  NOT IN ('N/A','') THEN 1 ELSE 0 END) *  5 +
    (CASE WHEN i.ingredients_raw  IS NOT NULL AND i.ingredients_raw        != ''              THEN 1 ELSE 0 END) *  5
  ) / 100.0)
FROM products p
JOIN servings sv ON sv.product_id = p.product_id AND sv.serving_basis = 'per 100 g'
JOIN nutrition_facts nf ON nf.product_id = p.product_id AND nf.serving_id = sv.serving_id
LEFT JOIN ingredients i ON i.product_id = p.product_id
WHERE p.product_id = sc.product_id
  AND p.is_deprecated IS NOT TRUE;
