-- Delete product_ingredient rows referencing junk ingredients
DELETE FROM product_ingredient
WHERE ingredient_id IN (
  SELECT ingredient_id FROM ingredient_ref
  WHERE name_en ~ '^\d+$' OR length(trim(name_en)) <= 1 OR name_en ~* '^(per 100|kcal|kj\b)'
);

-- Delete junk ingredient_ref entries
DELETE FROM ingredient_ref
WHERE name_en ~ '^\d+$' OR length(trim(name_en)) <= 1 OR name_en ~* '^(per 100|kcal|kj\b)';

-- Delete orphan ingredient_ref entries (known junk names from old enrichment)
DELETE FROM ingredient_ref
WHERE ingredient_id NOT IN (SELECT DISTINCT ingredient_id FROM product_ingredient)
  AND name_en IN ('5-Rybonukleotydy Disodowe', 'Lubczyku');

